draw(interp(B,C,-0.2)--interp(B,C,1.2),linewidth(0.7));
draw(A--O--B,linewidth(0.7));
draw(D--P--C,linewidth(0.7));
label("$O$",O,SE);
label("$P$",P,E);
label("$D$",D,NW);
label("$A$",A,NW);
label("$B$",B,S);
label("$C$",C,S);
label("2",(-0.45,0.95),E);
label("6",(3,0),N);
label("4",(5.55,1.95),E);

